; RUN: llvm-mc -triple eravm -filetype=asm %s -o - | FileCheck %s

  .text
foo:

; 3-operand form
  call      r3, @callee, @exception_handler
; 2-operand form
  call      r3, @callee
; 1-operand form
  call      @callee
  call      r0, @callee ; the same as above

; modifiers (condition)
  call.lt   r3, @callee, @exception_handler
  call.lt   r3, @callee
  call.lt   @callee


